import { Typography, Box, IconButton, Dialog, DialogTitle, DialogContent, DialogContentText, TextField, Button, DialogActions  } from '@mui/material';
import DeleteIcon from '@mui/icons-material/Delete';
import AddCircleOutlineIcon from '@mui/icons-material/AddCircleOutline';
import React, { useState } from 'react';

export interface RegistrationItemProps {
    cardId: string;
    date: string;
}

export const RegistrationItem: React.FC<RegistrationItemProps> = ({
    cardId,
    date,
}) => {

    const [modalIsOpen, setModalIsOpen] = useState<boolean>(false);
    const [ studnetName, setStudentName] = useState<string>('');

    const handleChange = (event: React.ChangeEvent<HTMLInputElement>) => {
        setStudentName(event.target.value);
      };

    const submitStudent = () => {
        if (studnetName === '') {
            alert('You didnt enter any name');
        } else {
            console.log(studentName)
            // TODO: add code to add student to db
        }        
        setModalIsOpen(false)
    }

    return (
        <>
    <Box sx={{
        display: 'flex',
        flexDirection: 'row',
        justifyContent: 'space-around', 
        alignItems: 'center',
        margin: '8px',
        width: 700,
        height: 70,
        border: '1px grey',
        borderRadius: '15px',
        backgroundColor: '#68A7AD',
        '&:hover': {
          backgroundColor: '#99C4C8',
          opacity: [0.9, 0.8, 0.7],
        },
      }}>
         <Typography> {cardId} </Typography>
         <Typography> {date} </Typography>
         <IconButton> <DeleteIcon/> </IconButton>
         <IconButton> <AddCircleOutlineIcon onClick={() => setModalIsOpen(true)}/> </IconButton>
    </Box>
    <Dialog open={modalIsOpen} onClose={() => setModalIsOpen(false)}>
        <DialogTitle>Subscribe</DialogTitle>
        <DialogContent>
          <DialogContentText>
            To conect this card id to student name, just enter the student name here :)
          </DialogContentText>
          <TextField
            autoFocus
            margin="dense"
            id="name"
            label="Enter Student Name"
            type="text"
            fullWidth
            variant="standard"
            onChange={handleChange}
          />
        </DialogContent>
        <DialogActions>
          <Button onClick={() => setModalIsOpen(false)}> Cancel </Button>
          <Button onClick={submitStudent}> Submit </Button>
        </DialogActions>
      </Dialog>
    </>
    );
}
