import { createTheme, responsiveFontSizes } from "@mui/material";

export const getAppTheme = () => {
  let theme = createTheme({
    palette: {
      primary: {
        main: "#24333c",
        light: "#24333c",
      },
      secondary: {
        main: "#743ca5",
        light: "#BB99CD",
        dark: "#BB99CD",
      },
    },
    typography: {
      fontFamily: "Assistant",
    },
    direction: "rtl",
  });

  theme = responsiveFontSizes(theme);
  return theme;
};
