import { Typography, Box, IconButton, Modal  } from '@mui/material';
import DeleteIcon from '@mui/icons-material/Delete';
import AddCircleOutlineIcon from '@mui/icons-material/AddCircleOutline';
import React, { useState } from 'react';

export interface RegistrationItemProps {
    cardId: string;
    date: string;
}

export const RegistrationItem: React.FC<RegistrationItemProps> = ({
    cardId,
    date,
}) => {

    const [modalIsOpen, setModalIsOpen] = useState<boolean>(false);


    return (
        <>
    <Box sx={{
        display: 'flex',
        flexDirection: 'row',
        justifyContent: 'space-around', 
        alignItems: 'center',
        margin: '8px',
        width: 700,
        height: 70,
        border: '1px grey',
        borderRadius: '15px',
        backgroundColor: '#68A7AD',
        '&:hover': {
          backgroundColor: '#99C4C8',
          opacity: [0.9, 0.8, 0.7],
        },
      }}>
         <Typography> {cardId} </Typography>
         <Typography> {date} </Typography>
         <IconButton> <DeleteIcon/> </IconButton>
         <IconButton> <AddCircleOutlineIcon onClick={() => setModalIsOpen(true)}/> </IconButton>
    </Box>
    <Modal
    open={modalIsOpen}
    onClose={() => setModalIsOpen(false)}
    aria-labelledby="modal-modal-title"
    aria-describedby="modal-modal-description"
    >
        <Box sx={modalStyle}>
            <Typography id="modal-modal-title" variant="h6" component="h2">
            Text in a modal
            </Typography>
            <Typography id="modal-modal-description" sx={{ mt: 2 }}>
            Duis mollis, est non commodo luctus, nisi erat porttitor ligula.
            </Typography>
        </Box>
    </Modal>
    </>);
}
