  import AddCircleOutlineIcon from "@mui/icons-material/AddCircleOutline";
  import React, { useState } from "react";
  import { addStudentDoc, deleteRegistratioDoc } from "../../firebase/dbRef";

  export interface HistoryItemProps {
    cardId: string;
    machineId: string;
    date: string;
    refetch: () => void;
